
public class Ex2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10000; //돈
		int b=1000;  //사과
		
		System.out.println(a/b);        //최대 구매 가능 갯수
		System.out.println(b*3);        //사과 3개의 가격
		System.out.println(a-(b*5));    //사과 5개를 사고 남는 금액
		System.out.println((b*5)*0.01); //사과 5개를 사고 쌓이는 적립금 (구매 금액의 1%)
	}
}

